function new_value(t, o) {
    var r = hexToR("#" + t);
    var g = hexToG("#" + t);
    var w = hexToB("#" + t);
    var s = new Array("");
    s = rgbToHsl(r, g, w);
    var u = s[2];
    if (s[2] > o) {
        u = s[2] - o
    }
    else {
        u = s[2] + o
    }
    var v = new Array("");
    v = hslToRgb(s[0], s[1], u);
    var x = Math.round(v[0]);
    var q = Math.round(v[1]);
    var p = Math.round(v[2]);
    var b = rgbtohexa(x, q, p);
    return b
}

function rgbtohexa(f, g, b) {
    var h = function(c) {
        var a = "0123456789abcdef";
        if (c == null) {
            return "00"
        }
        c = parseInt(c);
        if (c == 0 || isNaN(c)) {
            return "00"
        }
        c = Math.round(Math.min(Math.max(0, c), 255));
        return a.charAt((c - c % 16) / 16) + a.charAt(c % 16)
    };
    return h(f) + h(g) + h(b)
}

function rgbToHsl(r, l, g) {
    r /= 255, l /= 255, g /= 255;
    var d = Math.max(r, l, g),
            p = Math.min(r, l, g);
    var o, b, q = (d + p) / 2;
    if (d == p) {
        o = b = 0
    }
    else {
        var h = d - p;
        b = q > 0.5 ? h / (2 - d - p) : h / (d + p);
        switch (d) {
            case r:
                o = (l - g) / h + (l < g ? 6 : 0);
                break;
            case l:
                o = (g - r) / h + 2;
                break;
            case g:
                o = (r - l) / h + 4;
                break
        }
        o /= 6
    }
    return [o, b, q]
}

function hexToR(b) {
    return parseInt((cutHex(b)).substring(0, 2), 16)
}

function hexToG(b) {
    return parseInt((cutHex(b)).substring(2, 4), 16)
}

function hexToB(b) {
    return parseInt((cutHex(b)).substring(4, 6), 16)
}

function cutHex(b) {
    return (b.charAt(0) == "#") ? b.substring(1, 7) : b
}

function hslToRgb(p, b, q) {
    var v, l, g;
    if (b == 0) {
        v = l = g = q
    }
    else {
        function r(c, d, a) {
            if (a < 0) {
                a += 1
            }
            if (a > 1) {
                a -= 1
            }
            if (a < 1 / 6) {
                return c + (d - c) * 6 * a
            }
            if (a < 1 / 2) {
                return d
            }
            if (a < 2 / 3) {
                return c + (d - c) * (2 / 3 - a) * 6
            }
            return c
        }
        var u = q < 0.5 ? q * (1 + b) : q + b - q * b;
        var s = 2 * q - u;
        v = r(s, u, p + 1 / 3);
        l = r(s, u, p);
        g = r(s, u, p - 1 / 3)
    }
    return [v * 255, l * 255, g * 255]
}


